import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { useSpring, animated } from '@react-spring/web';

const Container = styled(animated.div)`
  margin: 20px;
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 10px;
  background-color: #fff;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease-in-out;

  &:hover {
    transform: scale(1.02);
  }
`;

const Title = styled.h2`
  color: #007bff;
  margin-bottom: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Input = styled.input`
  margin: 10px 0;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  background-color: #f9f9f9;
  color: #333;
  width: 80%;
  max-width: 400px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease-in-out, transform 0.3s ease-in-out;

  &:hover {
    background-color: #0056b3;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Response = styled(animated.p)`
  margin-top: 20px;
  color: #007bff;
  font-size: 1.2em;
`;

const PrescriptionUpload = () => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState('');
  const [responseVisible, setResponseVisible] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/upload_prescription', formData);
      setResponse(res.data.extracted_text);
      setResponseVisible(true);
    } catch (err) {
      console.error(err);
    }
  };

  const responseAnimation = useSpring({
    opacity: responseVisible ? 1 : 0,
    transform: responseVisible ? 'translateY(0)' : 'translateY(-20px)',
  });

  return (
    <Container>
      <Title>Upload Prescription</Title>
      <Form onSubmit={handleSubmit}>
        <Input type="file" onChange={handleFileChange} />
        <Button type="submit">Upload</Button>
      </Form>
      {response && (
        <Response style={responseAnimation}>Extracted Text: {response}</Response>
      )}
    </Container>
  );
};

export default PrescriptionUpload;
