import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { useSpring, animated } from '@react-spring/web';

const Container = styled(animated.div)`
  margin: 20px;
  padding: 20px;
  border: 1px solid #333;
  border-radius: 10px;
  background-color: #1e1e1e;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  transition: transform 0.3s ease-in-out;

  &:hover {
    transform: scale(1.02);
  }
`;

const Title = styled.h2`
  color: #007bff;
  margin-bottom: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Input = styled.input`
  margin: 10px 0;
  padding: 10px;
  border: 1px solid #555;
  border-radius: 5px;
  background-color: #2c2c2c;
  color: #e0e0e0;
  width: 80%;
  max-width: 400px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease-in-out, transform 0.3s ease-in-out;

  &:hover {
    background-color: #218838;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Response = styled(animated.p)`
  margin-top: 20px;
  color: #28a745;
  font-size: 1.2em;
`;

const DiagnosisForm = () => {
  const [symptoms, setSymptoms] = useState('');
  const [diagnosis, setDiagnosis] = useState('');
  const [responseVisible, setResponseVisible] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const symptomsArray = symptoms.split(',').map(Number);

    try {
      const res = await axios.post('http://localhost:5000/diagnose', { symptoms: symptomsArray });
      setDiagnosis(res.data.diagnosis);
      setResponseVisible(true);
    } catch (err) {
      console.error(err);
    }
  };

  const responseAnimation = useSpring({
    opacity: responseVisible ? 1 : 0,
    transform: responseVisible ? 'translateY(0)' : 'translateY(-20px)',
  });

  return (
    <Container>
      <Title>Diagnose Symptoms</Title>
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          value={symptoms}
          onChange={(e) => setSymptoms(e.target.value)}
          placeholder="Enter symptoms separated by commas"
        />
        <Button type="submit">Diagnose</Button>
      </Form>
      {diagnosis && (
        <Response style={responseAnimation}>Diagnosis: {diagnosis}</Response>
      )}
    </Container>
  );
};

export default DiagnosisForm;
